#include "libft.h"
#include <unistd.h>

void
	ft_putnbr_fd(int num, int fd)
{
	char	ch;

	if (num < 0)
		(void) write(fd, "-", 1);
	ch = '0' + (num % 10) * (1 - (num < 0) * 2);
	if (num >= 10)
		ft_putnbr_fd(num / 10, fd);
	if (num <= -10)
		ft_putnbr_fd(-(num / 10), fd);
	(void) write(fd, &ch, 1);
}
